const fs = require('fs'); 
const request = require('request');

const args = process.argv.slice(2);
// console.log(args); // [ 'http://www.example.edu/', './index.html' ]

const website = args[0]; 
// console.log(website); // 'http://www.example.edu/'

const file = args[1]; 
// console.log(file); // './index.html'

const writeWebsite = request(website, (error, response, body) => {
  console.log('error:', error); // Print the error if one occurred
  console.log('statusCode:', response && response.statusCode); // Print the response status code if a response was received
  console.log('body:', body); // Print the HTML for the Google homepage.
});
// console.log(writeWebsite); 

// fs.writeFile(filename, data[, options], callback)


/// test case:
// node fetcher.js http://www.example.edu/ ./index.html
//// print: Downloaded and saved 3261 bytes to ./index.html

fs.writeFile(file, 'writeWebsite', err => {
  const {size} = fs.statSync(file); 
  if (err) {
    console.error(err);
  }
  console.log(`Downloaded and saved ${size} bytes to ${file}`)
})